import { assert } from "chai";

import { numberToRpcQuantity } from "../../../../src/internal/core/providers/provider-utils";
import {
  dateToTimestampSeconds,
  parseDateString,
} from "../../../../src/internal/util/date";
import { HardhatNetworkUserConfig } from "../../../../src/types";
import { useEnvironment } from "../../../helpers/environment";
import { expectErrorAsync } from "../../../helpers/errors";
import { useFixtureProject } from "../../../helpers/project";

describe("Hardhat Network special options", function () {
  describe("allowUnlimitedContractSize", function () {
    // This is the compilation output of https://github.com/ethereum/solidity/blob/4d791b2679283d173896d118730c8b598bd2b7f6/test/libsolidity/syntaxTests/bytecode_too_large.sol
    const CONTRACT_BYTECODE =
      "";

    describe("When the setting is true", function () {
      useFixtureProject("unlimited-contract-size");
      useEnvironment();

      it("Should allow contracts larger than the EIP-170 limit", async function () {
        const [sender] = await this.env.network.provider.send("eth_accounts");
        await this.env.network.provider.send("eth_sendTransaction", [
          {
            from: sender,
            gas: numberToRpcQuantity(8000000),
            data: CONTRACT_BYTECODE,
          },
        ]);
      });
    });

    describe("When the setting is false", function () {
      useFixtureProject("default-config-project");
      useEnvironment();

      it("Should not allow contracts larger than the EIP-170 limit", async function () {
        const [sender] = await this.env.network.provider.send("eth_accounts");
        await expectErrorAsync(
          () =>
            this.env.network.provider.send("eth_sendTransaction", [
              {
                from: sender,
                gas: numberToRpcQuantity(8000000),
                data: CONTRACT_BYTECODE,
              },
            ]),
          /out of gas/
        );
      });
    });
  });

  describe("initialDate", function () {
    useFixtureProject("hardhat-network-initial-date");
    useEnvironment();

    it("Should set the blockchain date to the initialDate", async function () {
      const firstBlock = await this.env.network.provider.send(
        "eth_getBlockByNumber",
        ["latest", false]
      );

      await this.env.network.provider.send("evm_mine", []);

      const secondBlock = await this.env.network.provider.send(
        "eth_getBlockByNumber",
        ["latest", false]
      );

      const hardhatNetworkConfig = this.env.config.networks
        .hardhat as HardhatNetworkUserConfig;
      const initialDateString = hardhatNetworkConfig.initialDate!;
      const initialDate = dateToTimestampSeconds(
        parseDateString(initialDateString)
      );

      assert.equal(firstBlock.timestamp, initialDate);
      // We don't know the exact timestamp of the second one, but we can cap it
      assert.isAtLeast(+secondBlock.timestamp, initialDate + 1);
      assert.isBelow(+secondBlock.timestamp, initialDate + 20);
    });
  });
});
