import { assert } from "chai";

import { numberToRpcQuantity } from "../../../../src/internal/core/jsonrpc/types/base-types";
import { InternalError } from "../../../../src/internal/core/providers/errors";
import {
  dateToTimestampSeconds,
  parseDateString,
} from "../../../../src/internal/util/date";
import { HardhatNetworkConfig } from "../../../../src/types";
import { useEnvironment } from "../../../helpers/environment";
import { expectErrorAsync } from "../../../helpers/errors";
import { useFixtureProject } from "../../../helpers/project";
import { ALCHEMY_URL } from "../../../setup";

describe("Hardhat Network special options", function () {
  describe("allowUnlimitedContractSize", function () {
    // This is the compilation output of https://github.com/ethereum/solidity/blob/4d791b2679283d173896d118730c8b598bd2b7f6/test/libsolidity/syntaxTests/bytecode_too_large.sol
    const CONTRACT_BYTECODE =
      "";

    describe("When the setting is true", function () {
      useFixtureProject("unlimited-contract-size");
      useEnvironment();

      it("Should allow contracts larger than the EIP-170 limit", async function () {
        const [sender] = await this.env.network.provider.send("eth_accounts");
        await this.env.network.provider.send("eth_sendTransaction", [
          {
            from: sender,
            gas: numberToRpcQuantity(8000000),
            data: CONTRACT_BYTECODE,
          },
        ]);
      });
    });

    describe("When the setting is false", function () {
      useFixtureProject("default-config-project");
      useEnvironment();

      it("Should not allow contracts larger than the EIP-170 limit", async function () {
        const [sender] = await this.env.network.provider.send("eth_accounts");
        await expectErrorAsync(
          () =>
            this.env.network.provider.send("eth_sendTransaction", [
              {
                from: sender,
                gas: numberToRpcQuantity(8000000),
                data: CONTRACT_BYTECODE,
              },
            ]),
          /trying to deploy a contract whose code is too large/
        );
      });
    });
  });

  describe("initialDate", function () {
    useFixtureProject("hardhat-network-initial-date");
    useEnvironment();

    it("Should set the blockchain date to the initialDate", async function () {
      const firstBlock = await this.env.network.provider.send(
        "eth_getBlockByNumber",
        ["latest", false]
      );

      await this.env.network.provider.send("evm_mine", []);

      const secondBlock = await this.env.network.provider.send(
        "eth_getBlockByNumber",
        ["latest", false]
      );

      const hardhatNetworkConfig = this.env.config.networks
        .hardhat as HardhatNetworkConfig;
      const initialDateString = hardhatNetworkConfig.initialDate!;
      const initialDate = dateToTimestampSeconds(
        parseDateString(initialDateString)
      );

      assert.equal(firstBlock.timestamp, initialDate);
      // We don't know the exact timestamp of the second one, but we can cap it
      assert.isAtLeast(+secondBlock.timestamp, initialDate + 1);
      assert.isBelow(+secondBlock.timestamp, initialDate + 20);
    });
  });

  describe("hardfork", function () {
    describe("When not forking", function () {
      useFixtureProject("hardhat-network-spurious-dragon");
      useEnvironment();

      // We skip this test as we are temporally disabling older hardforks due
      // to a bug in EthereumJS.
      // TODO: Remove the skip once the DefaultStateManager is fixed
      it.skip("should accept hardforks as late as chainstart and use them", async function () {
        const [sender] = await this.env.network.provider.send("eth_accounts");
        await assert.isRejected(
          this.env.network.provider.send("eth_sendTransaction", [
            {
              from: sender,
              // This is a deployment with a constructor that just executes CHAIN_ID
              // which was added in Istanbul
              data: "0x46",
            },
          ]),
          "Transaction reverted"
        );
      });
    });

    describe("When forking", function () {
      if (ALCHEMY_URL === undefined) {
        return;
      }

      // We skip this test as we are temporally disabling older hardforks due
      // to a bug in EthereumJS.
      // TODO: Remove the skip once the DefaultStateManager is fixed
      describe.skip("Local hardfork validation", function () {
        useFixtureProject("hardhat-network-fork-tangerine-whistle");
        useEnvironment();

        it("Shouldn't work with hardforks before spurious dragon", async function () {
          await assert.isRejected(
            this.env.network.provider.send("eth_accounts"),
            InternalError,
            "Invalid hardfork"
          );
        });
      });

      describe("Remote hardfork validation", function () {
        useFixtureProject("hardhat-network-fork-from-old-block");
        useEnvironment();

        it("Shouldn't work with block numbers from before spurious dragon", async function () {
          await assert.isRejected(
            this.env.network.provider.send("eth_accounts"),
            InternalError,
            "Cannot fork mainnet from block"
          );
        });
      });
    });
  });

  describe("coinbase", function () {
    useFixtureProject("hardhat-network-with-custom-coinbase");
    useEnvironment();

    it("Should use the 0 address for the genesis block", async function () {
      const block = await this.env.network.provider.send(
        "eth_getBlockByNumber",
        [numberToRpcQuantity(0), false]
      );

      assert.equal(block.miner, "0x0000000000000000000000000000000000000000");
    });

    it("Should use the coinbase address for the new blocks", async function () {
      await this.env.network.provider.send("evm_mine");

      const block = await this.env.network.provider.send(
        "eth_getBlockByNumber",
        [numberToRpcQuantity(1), false]
      );

      assert.equal(block.miner, this.env.config.networks.hardhat.coinbase);
    });
  });

  describe("accounts", function () {
    describe("mnemonic without passphrase", async function () {
      useFixtureProject("mnemonic-without-passphrase");
      useEnvironment();

      it("should generate the correct accounts", async function () {
        const [a1, a2] = await this.env.network.provider.send("eth_accounts");

        assert.equal(
          a1.toLowerCase(),
          "0x57d27a673dbd70f12cbc32211c86af726683ce25"
        );
        assert.equal(
          a2.toLowerCase(),
          "0x075d1d9101a6ed3dcdc2f41c401cf638a7bbe053"
        );
      });
    });

    describe("mnemonic with a passphrase", async function () {
      useFixtureProject("mnemonic-with-passphrase");
      useEnvironment();

      it("should generate the correct accounts", async function () {
        const [a1, a2] = await this.env.network.provider.send("eth_accounts");

        assert.equal(
          a1.toLowerCase(),
          "0x43da4d822743814bf08648631afe7854bb57d8dc"
        );
        assert.equal(
          a2.toLowerCase(),
          "0x1df58751bbe561bae7a82b55485c0e076748eedb"
        );
      });
    });
  });
});
